mapper.select:
select idle.*, student.user_name,student.user_icon,student.credit,student.student_id
from idle_info idle
left join classify on (idle.classify_id = classify.classify_id)
left join student on (idle.user_id = student.user_id)
where idle.school_id = #{schoolId} and idle.status not in (100,101)

	and idle.create_date <= #{createDate}


	and idle.status = #{status}


	and (title like "%"#{search}"%" or idel_info like "%"#{search}"%" or classify.classify_name like "%"#{search}"%")


	and idle.user_id = #{userId}

order by idle.create_date desc
limit #{start},#{pageSize}

select idle_info.*,classify.classify_name,student.user_name,school.school_name from idle_info left join classify on (idle_info.classify_id = classify.classify_id) left join student on (idle_info.user_id = student.user_id) left join school on (student.school_id = school.school_id) where idle_info.status not in (100,101)
and (idle_info.title like "%"#{searchString}"%" or idle_info.address like "%"#{searchString}"%" or idle_info.idel_info like "%"#{searchString}"%" or classify.classify_name like "%"#{searchString}"%" or student.user_name like "%"#{searchString}"%" or school.school_NAME like "%"#{searchString}"%")
limit #{start},#{length}

select idle_info.*,classify.classify_name,student.user_name,school.school_name from idle_info left join classify on (idle_info.classify_id = classify.classify_id) left join student on (idle_info.user_id = student.user_id) left join school on (student.school_id = school.school_id) where idle_info.status = 100
and (idle_info.title like "%"#{searchString}"%" or idle_info.address like "%"#{searchString}"%" or idle_info.idel_info like "%"#{searchString}"%" or classify.classify_name like "%"#{searchString}"%" or student.user_name like "%"#{searchString}"%" or school.school_NAME like "%"#{searchString}"%")
limit #{start},#{length}
